--
-- Структура таблицы `stages`
--

CREATE TABLE `stages` (
  `id` int(10) UNSIGNED NOT NULL,
  `stage` varchar(400) NOT NULL,
  `stage_description` varchar(1000) NOT NULL,
  `stage_table_name` varchar(400) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--
-- Дамп данных таблицы `stages`
--

INSERT INTO `stages` (`id`, `stage`, `stage_description`, `stage_table_name`) VALUES
(1, '1. Начало урока', 'Грамотная организация начала урока позволит не только привлечь внимание учащихся к учителю, заинтересовать учащихся, но и включить детей в активную мыследеятельность с первых минут занятия.', 'beginning'),
(2, '2. Актуализация знаний', 'Учитель организует:\r\n<ul>\r\n<li>актуализацию изученных способов действий, достаточных для построения нового знания, их обобщение и знаковую фиксацию;</li>\r\n<li>актуализацию соответствующих мыслительных операций и познавательных процессов;</li>\r\n<li>мотивацию к пробному учебному действию (“надо” - “могу” - “хочу”) и его самостоятельное осуществление.</li>\r\n</ul>', 'actualization'),
(3, '3. Изучение нового материала', 'На данном этапе учитель организует изучение нового материала через учебную деятельность школьников.', 'new_material'),
(4, '4. Обсуждение и решение проблем', 'На данном этапе учащиеся в коммуникативной форме обдумывают проект будущих учебных действий: ставят цель (целью всегда является устранение возникшего затруднения), согласовывают тему урока, выбирают способ, строят план достижения цели и определяют средства- алгоритмы, модели и т.д. Этим процессом руководит учитель: на первых порах с помощью подводящего диалога, затем – побуждающего, а затем и с помощью исследовательских методов.', 'problem_solving'),
(5, '5. Решение учебных задач', 'При проведении данного этапа используется индивидуальная форма работы: учащиеся самостоятельно выполняют задания нового типа и осуществляют их самопроверку, пошагово сравнивая с эталоном. В завершение организуется исполнительская рефлексия хода реализации построенного проекта учебных действий и контрольных процедур.\r\n<br><br>\r\nЭмоциональная направленность этапа состоит в организации, по возможности, для каждого ученика ситуации успеха, мотивирующей его к включению в дальнейшую познавательную деятельность.', 'learning_tasks'),
(6, '6. Контроль знаний, обратная связь', 'На данном этапе выявляются границы применимости нового знания и выполняются задания, в которых новый способ действий предусматривается как промежуточный шаг.\r\n<br><br>\r\nОрганизуя этот этап, учитель подбирает задания, в которых тренируется использование изученного ранее материала, имеющего методическую ценность для введения в последующем новых способов действий. Таким образом, происходит, с одной стороны, автоматизация умственных действий по изученным нормам, а с другой – подготовка к введению в будущем новых норм.', 'knowledge_control'),
(7, '7. Формирование умения задавать вопросы', 'Формируется умение задавать вопросы.', 'questions'),
(8, '8. Рефлексия', 'На данном этапе фиксируется новое содержание, изученное на уроке, и организуется рефлексия и самооценка учениками собственной учебной деятельности. В завершение соотносятся ее цель и результаты, фиксируется степень их соответствия, и намечаются дальнейшие цели деятельности.', 'reflection');

